// Enhance filteredEquipment() function
function filteredEquipment() {
  const q = normalize(state.searchEquipment);
  let equipment = state.equipment;

  // Apply mapping filter
  if (state.equipmentFilter === 'mapped') {
    equipment = equipment.filter(e => e.mapped);
  } else if (state.equipmentFilter === 'unmapped') {
    equipment = equipment.filter(e => !e.mapped);
  }

  return equipment.filter(e => q ? normalize(e.name).includes(q) : true);
}
