function scrollEquipmentToCenter(equipmentId) {
  const element = document.querySelector(`[data-equipment-id="${equipmentId}"]`);
  if (element && element.parentElement) {
    element.scrollIntoView({ 
      behavior: 'smooth', 
      block: 'center',
      inline: 'nearest'
    });
  }
}

function highlightMappedEquipment(equipmentId, deviceName) {
  // Clear previous highlights
  document.querySelectorAll('.equipment-highlight').forEach(el => {
    el.classList.remove('equipment-highlight');
  });

  // Highlight target equipment
  const element = document.querySelector(`[data-equipment-id="${equipmentId}"]`);
  if (element) {
    element.classList.add('equipment-highlight');
    state.highlightedEquipmentId = equipmentId;
    setTimeout(() => {
      element.classList.remove('equipment-highlight');
      state.highlightedEquipmentId = null;
    }, 3000);
  }
}
```

