// Replace existing device click handler around line 1544
document.querySelectorAll('#deviceList .item').forEach(el => el.addEventListener('click', () => {
  clearFloatingConnections();
  const deviceId = el.dataset.deviceId;
  const device = state.devices.find(d => d.id === deviceId);

  if (state.selectedDeviceId === deviceId) {
    // Deselect if clicking same device
    state.selectedDeviceId = null;
    state.selectedEquipmentId = null;
  } else {
    state.selectedDeviceId = deviceId;

    if (device && device.mappedTo) {
      // MAPPED DEVICE: scroll to equipment and highlight
      state.selectedEquipmentId = device.mappedTo;
      setTimeout(() => {
        scrollEquipmentToCenter(device.mappedTo);
        highlightMappedEquipment(device.mappedTo, device.name);
      }, 100);
    } else {
      // UNMAPPED DEVICE: existing behavior (confidence lines)
      autoSelectSuggestedEquipment();
    }
  }
  render();
}));
```

